import React from "react";

export class App extends React.Component {
  state = {
    good: 0,
    neutral: 0,
    bad: 0
  }

  addFeedback(e) {
    this.setState()
  }


  render() {
    const { good, neutral, bad } = this.state;
    const visibleFeedback = this.addFeedback();

    return <div>
      <h1>Please leave feedback</h1>
      <ul>
        <li><button type="button" onClick={visibleFeedback}>Good</button></li>
        <li><button type="button" onClick={visibleFeedback}>Neutral</button></li>
        <li><button type="button" onClick={visibleFeedback}>Bad</button></li>
      </ul>
      <h2>Statistics</h2>
      <ul>
        <li><p>Good:{good}</p></li>
        <li><p>Neutral:{neutral}</p></li>
        <li><p>Bad:{bad}</p></li>
      </ul>

    </div>
  }
}
