import React from "react";
import Feedback from "./Feedback";

export class App extends React.Component {
  state = {
    good: 0,
    neutral: 0,
    bad: 0
  }

  addFeedback = e => {
    this.setState({ [e]: this.state[e] + 1 });
  }

  countTotalFeedback = ({ good, neutral, bad }) =>
    good + neutral + bad;


  countPositiveFeedbackPercentage() {
    return Math.round((this.state.good * 100) / (this.state.neutral + this.state.bad + this.state.good))
  }


  render() {
    const { good, neutral, bad } = this.state;
    const options = Object.keys(this.state)
    const countTotalFeedback = this.countTotalFeedback()
    const countPositiveFeedbackPercentage = this.countPositiveFeedbackPercentage()

    return <div>
      <h1>Please leave feedback</h1>
      <ul>
        <Feedback options={options}
        addFeedback={this.addFeedback}/>
      </ul>
      <h2>Statistics</h2>
      <ul>
        <li><p>Good:{good}</p></li>
        <li><p>Neutral:{neutral}</p></li>
        <li><p>Bad:{bad}</p></li>
        <li><p>Total:{countTotalFeedback}</p></li>
        <li><p>Positive feedback:{countPositiveFeedbackPercentage}</p></li>
      </ul>

    </div>
  }
}
